import React, { Component } from "react";

class Counter extends Component {
  state = { count: 0,imageUrl: "https://picsum.photos/200", tags: ["tag1", "tag2", "tag3", "tag4"] };

  handleIncrement = () => {
      this.state.count++;

      this.setState({count: this.state.count + 1})
  }

  render() {
    let classes = "badge m-2 badge-";
    classes += this.state.count === 0 ? "warning" : "primary"; 


    return (

      <>
        {this.state.tags.legth === 0 && "no String"}
        <img src={this.state.imageUrl} />
        <span className={classes}>{this.formatCount()}</span>
        <button className="btn btn-secondary btn-sm" onClick={this.handleIncrement()}>Increment</button>
        <ul>
        {this.state.tags.map((tag) => (<li key={tag}>{tag}</li>))}
        </ul>
      </>

    );
  }

  formatCount() {
    // object destructuring in action
    const { count } = this.state;
    return count === 0 ? <h1>Zero</h1> : count;
  }

}

export default Counter;
